mapper.select:
select mr.name, temp.meeting from tb_meeting_room mr left join ( select place, CONCAT( "[", GROUP_CONCAT( JSON_OBJECT('start', DATE_FORMAT(start, '%H:%i'), 'end', DATE_FORMAT(end, '%H:%i'), 'status', `status`, 'time', ROUND(timestampdiff(MINUTE,CONCAT(date,"",start),CONCAT(date,"",end))/30) ) ),"]" ) as meeting from tb_meeting where type = 2 and `status` != 2 and date=#{date}
and JSON_CONTAINS(mebers, CAST(#{userId} as CHAR))
GROUP BY place ) temp on mr.name=temp.place where mr.status=1 limit #{start}, #{length}

mapper.update:
update tb_meeting
set instance_id=#{instanceId}
where uuid=#{uuid}

mapper.select:
select m.title,
       m.date,
       m.place,
       date_format(m.`start`,'%H:%i') as `start`,
       date_format(m.`end`,'%H:%i') as `end`,
       m.status,
       (
          select concat("[", group_concat( json_object( "name", u,name, "photo", u.photo)) separator '，')
          from tb_user u where json_contains(m.present, convert(u.id, char))
        ) as present,
        (
          select concat("[", group_concat( json_object("name", u.name, "photo", u.photo)) separator '，')
          from tb_user u where json_contains(m.unpresent, convert(u.id, char))
        ) as unpresent
from tb_meeting m
where m.id = #{id}

mapper.delete:
delete
from tb_meeting
where id=#{id} and creator_id=#{creatorId};
