mapper.select:
SELECT id
FROM tb_user
WHERE username=#{username}
  AND password=HEX(AES_ENCRYPT(#{password},#{username}))
LIMIT 1;

SELECT DISTINCT u.id, u.name, u.sex, u.tel, u.email, d.dept_name AS dept, u.hiredata, u.root, u.status, ( SELECT GROUP_CONCAT( role_name separator ",") FROM tb_role WHERE JOSN_CONTAINS( u.role, CONVERT(id, CHAR)) ) AS roles FROM tb_user u JOIN tb_role r ON JSON_CONTAINS (u.role, CONVERT( r.id, CHAR)) left join tb_dept d on u.dept_id = d.id where 1=1
and u.name LIKE "%${name}%"
and u.sex=#{sex}
and u.role=#{role]}
and u.id=#{deptId}
and u.status=#{status}
LIMIT #{start}, #{length}

SELECT
    count(*)
FROM tb_user u
join tb_role r on JSON_CONTAINS(u.role, CONVERT(r.id, CHAR))
WHERE 1=1

    and u.name=#{name}


    and u.sex=#{sex}


    and u.role=#{role}


    and u.dept_id=#{deptId}


    and u.status=#{status}
